import React, { useEffect } from 'react'
import { fetchDataAsync, selectData,} from './chartSlice';
import { Chart } from "react-google-charts";
import { useSelector, useDispatch } from 'react-redux';

export function ChartData() {
  const dispatch = useDispatch();
  const data = useSelector(selectData);

  useEffect(() => {
    dispatch(fetchDataAsync())
  }, [dispatch])

  const chartData = data.map((item) => ([item.date, item.totalPredictedJobsViews, item.totalViews, item.totalJobs]));

  const dataArray = [
    ["Date", "Predicated job views", "Job views", "Active jobs" ],
    ...chartData
  ];

   const options = {
    title: "Cumulative job views vs. prediction",
    curveType: "function",
    seriesType: "bars",
    series: {
      0: { color: '#00CED1' , type: "line" },
      1: { color: '#228B22' , type: "line" },
      2: { color: '#DCDCDC' },
    },
    // intervals: { style: "points", pointSize: 20 },
    legend: { position: "bottom" },
    pointsVisible: true,
    pointSize: 6,
  };

  return (
    <div>
      <Chart
      chartType="ComboChart"
      width="100%%"
      height="500px"
      data={dataArray}
      options={options} />
    </div>
  );
}
